import React from 'react';
import ReactDOM from 'react-dom';

class StudentList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            slist: []
        };

    }

    componentDidMount() {
        fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Students/')
            .then(res => res.json())
            .then((data) => {
                this.setState({ slist: data })
            })
            .catch(console.log)

        {

    }

    }



    render() {
        return (
            <div>

                <p>Student Data

                </p>
                <table className="wrapped confluenceTable tablesorter tablesorter-default">
                    <thead>

                        <tr className="tablesorter-headerRow">
                            <th >
                                Student Name <span id="pRowId">{this.props.rowId}</span>
                            </th>
                            <th>
                                Age
                            </th>
                            <th>
                                GPA
                            </th>
                            <th>


                            </th>
                            <th>


                            </th>

                        </tr>
                    </thead>
                    <tbody>
                        {

                            this.state.slist.filter(function (student) { return student.clsId === parseInt(document.getElementById('pRowId').textContent) }).map(el => (
                              
                                <tr key={el.stuId}>
                                    

                                    <td>
                                        {el.stuFName} {el.stuLName}
                                    </td>
                                    <td>
                                        {el.stuAge}
                                    </td>
                                    <td>
                                        {el.stuGpa}
                                    </td>
                                    <td>
                                        edit
                                </td>
                                    <td>
                                        delete
                                </td>
                                </tr>
                            ))
                        }
                    </tbody>
                </table>



                <p>{/*this.state.slist.map(exp1 => exp1.filter(exp2 => exp2.stuAge > 20))*/



                }</p>










            </div>


        );

    }
}

export default StudentList;
