import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';
import { Redirect, Link } from "react-router-dom";


class ClassesList extends React.Component {
    constructor(prop) {
        super(prop);
        this.state = { error: null, isLoaded: false, items: [], rid :0 };
    }

    async componentDidMount() {
        try {
            const response = await fetch('http://localhost:55217/api/folioclasses');
            const result = await response.json();
            this.setState({ isLoaded: true, items: result.item1 });
        } catch (error) {
            this.setState({ error: true })
            console.log(error);
        }
    }

    deleteClass(id) {
        console.log(id);
        //let arrayClass = [...props.clist];

        // let classIndex = arrayClass.findIndex(a => a.clsId === id);
        // if (classIndex !== -1) {
        // arrayClass.splice(classIndex, 1);

        // }
    }

    handleClick(id){
       
       this.setState({rid:id});
    }


    render() {

        let { error, isLoaded, items } = this.state;
        if (error) {
            return (
                <div>
                    <p>
                        Error: {error}
                    </p>
                </div>
            )
        }
        else {
            return (
                <div>
                    <p>Class Data</p>
                    <table className="table table-bordered">
                        <thead>

                            <tr>
                                <th> Class Name</th>
                                <th>Location</th>
                                <th>Teacher Name</th>
                                <th></th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                items.map(el => (

                                    <tr key={el.id}>
                                        <td onClick={() => this.handleClick(el.id)} style={{ cursor: 'pointer' }}>
                                            {el.name}
                                        </td>
                                        <td onClick={() => this.handleClick(el.id)} style={{ cursor: 'pointer' }}>
                                            {el.location}
                                        </td> 
                                        <td>
                                            {el.teacherName}
                                        </td>
                                        <td>
                                            edit
                                </td>
                                        <td>
                                            <button
                                                onClick={() => this.deleteClass(el.id)}
                                                className="btn btn-light"
                                            >
                                                Delete
                                        </button>
                                        </td>
                                    </tr>))
                            }
                        </tbody>
                    </table>
                   
                   <link to="">Add1</link>

                    <button type="button" class="btn btn-light">ADD</button>
                    
                    


                    {this.state.rid > 0 ? <StudentList rowId={this.state.rid} /> : " "}


                   



                  
                </div>



            );
        }
    }


}
export default ClassesList;
