import React from 'react';

class ClassesForm extends React.Component {
    constructor(props) {
        super(props); 
        let fClass = {
            id: null, 
            cName : "",
            location: "",
            fName: "",
            lName: "",
            sal: ""        
        }
        this.state={classData:fClass};
       
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);     
    }

    handleChange(event) {
        debugger;
        let feildName = event.target.name
        this.setState({[this.state.classData.feildName]: event.target.value});
      }

      handleSubmit(event) {
        event.preventDefault();
        debugger;
    

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify()
        };
        fetch('http://localhost:55217/api/folioclasses', requestOptions)
            .then(response => response.json());
            debugger;
        console.log(JSON.stringify(this.state.classData));       
      }   

      componentDidMount () {
      
          console.log("props", this.props.id);
          


        // from the path `/inbox/messages/:id`
        // var id = this.props.params.id
        // alert(id)
    }
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <table>
                <tr>
                    <td><span>Class Name:</span></td>
                    <td><input type="text" name="cname" value={this.state.classData.cName} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td><span>Location:</span></td>
                    <td><input type="text" name="location" value={this.state.classData.location} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td><span>First Name:</span></td>
                    <td><input type="text" name="fName" value={this.state.classData.fName} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td><span>Last Name:</span></td>
                    <td><input type="text" name="lName" value={this.state.classData.lName} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td>
                        Select
                  </td>

                    <td>
                        <select value={this.state.classData.value} onChange={this.handleChange}>
                            <option value="Mr">Mr</option>
                            <option value="Miss">MIss</option>
                            <option value="Dr">Dr</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td>
                        <input type="submit" value="Submit" />
                    </td>
                </tr>
                </table>
            </form>
        );
    }
}

export default ClassesForm;
