import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";



const initialFieldValues = {

    id: 0,
    cname: "",
    location: "",
    fName: "",
    lName: "",
    sal: "",
}

const ClassesForm = ({ classes, ...props }) => {


    



    const handleSubmit = e => {
        e.preventDefault()
        if (1 ===1) {
            const onSuccess = () => {
                resetForm()
                //addToast("Submitted successfully", { appearance: 'success' })
            }
            if (props.currentId == 0)
                props.createClass(values, onSuccess)
            else
                props.updateClass(props.currentId, values, onSuccess)
        }
    }

    useEffect(() => {
        if (props.currentId != 0) {
            setValues({
                ...props.dCandidateList.find(x => x.id == props.currentId)
            })
            setErrors({})
        }
    }, [props.currentId])

    return (
        <form className={classes.root} onSubmit={handleSubmit}>
            <tr>
                <td><span>Class Name:</span></td>
                <td><input type="text" name="cname" value={cName} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>Location:</span></td>
                <td><input type="text" name="location" value={location} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>First Name:</span></td>
                <td><input type="text" name="fName" value={fName} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>Last Name:</span></td>
                <td><input type="text" name="lName" value={lName} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>Salutation:</span></td>
                <td><input type="text" name="sal" value={sal} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td>
                    Submit
                </td>
            </tr>
        </form>
    );

}

export default ClassesForm;
