import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";



const initialFieldValues = {

    id: 0,
    cname: "",
    location: "",
    fName: "",
    lName: "",
    sal: "",
}

const ClassesForm = ({ classes, ...props }) => {

    const ValidateForm = (fieldValues = values) => {
        let temp = { ...errors }

        if ('name' in fieldValues)
            temp.cname = fieldValues.cname ? "" : "This field is required."
        if ('location' in fieldValues)
            temp.location = fieldValues.location ? "" : "This field is required."
        if ('fName' in fieldValues)
            temp.fName = fieldValues.fName ? "" : "This field is required."
        if ('lName' in fieldValues)
            temp.lName = fieldValues.lName ? "" : "This field is required."
        if ('Sal' in fieldValues)
            temp.Sal = fieldValues.Sal ? "" : "This field is required."

        setErrors({
            ...temp
        })

        if (fieldValues == values)
            return Object.values(temp).every(x => x == "")
    }


   
   


    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            const onSuccess = () => {
                resetForm()
                //addToast("Submitted successfully", { appearance: 'success' })
            }
            if (props.currentId == 0)
                props.createClass(values, onSuccess)
            else
                props.updateClass(props.currentId, values, onSuccess)
        }
    }

    useEffect(() => {
        if (props.currentId != 0) {
            setValues({
                ...props.dCandidateList.find(x => x.id == props.currentId)
            })
            setErrors({})
        }
    }, [props.currentId])

    return (
        <form>
            <tr>
                <td><span>Class Name:</span></td>
                <td><input type="text" name="cname" value={values.cName} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>Location:</span></td>
                <td><input type="text" name="location" value={values.location} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>First Name:</span></td>
                <td><input type="text" name="fName" value={values.fName} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>Last Name:</span></td>
                <td><input type="text" name="lName" value={values.lName} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td><span>Salutation:</span></td>
                <td><input type="text" name="sal" value={values.sal} onChange={handleInputChange}></input>
                   
                </td>
            </tr>
            <tr>
                <td>
                    Submit
                </td>
            </tr>
        </form>
    );

}

export default ClassesForm;
