import React, { useState, useEffect } from 'react';
import DDClassName from '../DDClassName';

const StudentAdd = props => {
    const initialFormState = { id: null, firstName: '', lastName: '', age: '', gpa: '', folioClassId: '' }
    const [user, setUser] = useState(initialFormState);
    const [clst, setClst] = useState([]);
    const [tClass, selectedTClass] = useState("");
    const [vError, validationError] = useState("");


    // For Dropdown list
    useEffect(() => {
        fetch(
            "http://localhost:55217/api/folioclasses"
          )
            .then(response => {
              return response.json();
            })
            .then(data => {
                debugger;
              let teamsFromApi = data.item1.map(team => {
                return { value: team.id, display: team.name };
              });
              this.setState({
                clst: [
                  {
                    value: "",
                    display:
                      "(Select Class)"
                  }
                ].concat(teamsFromApi)
              });
            })
            .catch(error => {
              console.log(error);
            });    
            
      });
  

    const handleInputChange = event => {
        const { name, value } = event.target;

        if (name === "age") {
            if (value < 1 || value > 100) {
                user.age = "";
                alert("Not valid age");
            }
        }

        if (name === "lastName") {

            if (props.stuList.map(el => (el.lastName).toUpperCase()).indexOf(value.toUpperCase()) !== -1) {

                alert("Please change the last name");
                user.lastName = "";

            } else {

                setUser({ ...user, [name]: value });

            }
        }
        else {
            setUser({ ...user, [name]: value });
        }



    }

    return (
        <form
            onSubmit={event => {
                event.preventDefault()
                if (!user.firstName || !user.lastName || !user.age || !user.gpa || user.folioClassId) {
                    alert("Fill Form");
                    return
                }
                const requestOptions = {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(user)
                };
                fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Student', requestOptions)
                    .then(response => response.json());
                console.log(JSON.stringify(user));

                setUser(initialFormState)


            }}
        >
            <tr>
                <td>
                    <label>First Name</label>

                </td>
                <td>
                    <input type="text" name="firstName" value={user.firstName} onChange={handleInputChange} />

                </td>
            </tr>

            <tr>
                <td>
                    <label>Last Name</label>
                </td>
                <td>
                    <input type="text" name="lastName" value={user.lastName} onChange={handleInputChange} />
                </td>
            </tr>
            <tr>
                <td> <label>Age</label></td>
                <td><input type="number" pattern="[0-9]*" inputmode="numeric" name="age" value={user.age} onChange={handleInputChange} /></td>
            </tr>
            <tr>
                <td><label>GPA</label></td>
                <td><input type="number" pattern="[0-9]*" inputmode="numeric" name="gpa" value={user.gpa} onChange={handleInputChange} /></td>
            </tr>
            <tr>
                <td>
                    <label>Class Name</label>

                </td>
                <td>
                <div>
          <select
            value={this.state.selectedTClass}
            onChange={e =>
              this.setState({
                selectedTClass: e.target.value,
                validationError:
                  e.target.value === ""
                    ? "You must select class"
                    : ""
              })
            }
          >
            {this.state.clst.map(team => (
              <option
                key={team.value}
                value={team.value}
              >
                {team.display}
              </option>
            ))}
          </select>
          <div
            style={{
              color: "red",
              marginTop: "5px"
            }}
          >
            {this.state.validationError}
          </div>
        </div>
                </td>
            </tr>
            <tr>
                <td>
                    <button>Add Student</button>
                </td>
            </tr>

            






        </form>
    )
}

export default StudentAdd;
