import React from 'react';

class ClassesForm extends React.Component {
    constructor(props) {
        super(props);
        let fClass = {
            id: null,
            cname: "",
            location: "",
            fName: "",
            lName: "",
            sal: ""
        }
        this.state = { classData: fClass };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        let obj = this.state.classData;
        obj[event.target.name] = event.target.value;
        this.setState({ classData: obj });

    }

    

     handleSubmit (event) {
         
        event.preventDefault();

        const { name, value } = event.target;

        if (name === 'cname'){
            if (value.length === 0){
                alert("Enter name");
            }
        }
       



        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ 'folioClass': this.state.classData })
        };

        fetch('', requestOptions)
            .then(response => response.json());


    }

    componentDidMount() {
        console.log("props", this.props.id);
    }
    render() {
        return (
            <form onSubmit={this.handleSubmit}>
                <table className="table table-bordered table-sm ">
                    <tr>
                        <td><span>Class Name:</span></td>
                        <td><input  type="text" name="cname" value={this.state.classData.cName} onChange={this.handleChange}></input></td>                        
                    </tr>
                    <tr>
                        <td><span>Location:</span></td>
                        <td><input type="text" name="location" value={this.state.classData.location} onChange={this.handleChange}></input></td>
                    </tr>
                    <tr>
                        <td>
                            Select
                        </td>
                        <td>
                            <select name="sal" value={this.state.classData.value} onChange={this.handleChange}>
                                <option value="Select">Select</option>
                                <option value="Mr">Mr</option>
                                <option value="Miss">MIss</option>
                                <option value="Dr">Dr</option>
                            </select>
                        </td>
                    </tr>
                    <tr>
                        <td><span>First Name:</span></td>
                        <td><input  type="text" name="fName" value={this.state.classData.fName} onChange={this.handleChange}></input></td>                        
                    </tr>
                    <tr>
                        <td><span>Last Name:</span></td>
                        <td><input  type="text" name="lName" value={this.state.classData.lName} onChange={this.handleChange}></input></td>                        
                    </tr>
                    <tr>
                        <td colSpan="2">
                            <input type="submit" value="Submit" />
                        </td>
                    </tr>
                </table>
            </form>
        );
    }
}

export default ClassesForm;
