import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';

class ClassesList extends React.Component {
    constructor(prop) {
        super(prop);
        this.state = { error: null, isLoaded: false, items: [] };
    }

    async componentDidMount() {
        try {
            const response = await fetch('http://localhost:55217/api/folioclasses');
            const result = await response.json();
            this.setState({ isLoaded: true, items: result.item1 });
        } catch (error) {
            this.setState({ error: true })
            console.log(error);
        }
    }

    render() {

        const { error, isLoaded, items } = this.state;
        if (error) {
            return (
                <div>
                    <p>
                        Error: {error}
                    </p>
                </div>
            )
        }
        else {
            return (
                <div>
                    <p>Class Data</p>
                    <table className="table table-bordered">
                        <thead>

                            <tr>
                                <th> Class Name</th>
                                <th>Location</th>
                                <th>Teacher Name</th>
                                <th></th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                items.map(el => (

                                    <tr key={el.id}>
                                        <td>
                                            {el.name}
                                        </td>
                                        <td>
                                            {el.location}
                                        </td>
                                        <td>
                                            {el.teacherName}
                                        </td>
                                        <td>
                                            edit
                                </td>
                                        <td>
                                            <button
                                                onClick={() => this.deleteStudent(el.id)}
                                                className="btn btn-light"
                                            >
                                                Delete
                                        </button>
                                        </td>
                                    </tr>))

                            }
                        </tbody>
                    </table>
                </div>



            );
        }
    }


}
export default ClassesList;
