export const getClassList = async () => {
    try {
        const response = await fetch('http://localhost:55217/api/folioclasses');
        const result = await response.json();
        return result;

    } catch (error) {

        console.log(error);
    }
}

export const getStudentList = async () => {
    try {
        const response = await fetch('http://localhost:55217/api/Students');
        const result = await response.json();
        return result;

    } catch (error) {
        console.log(error);
    }
}

export const saveStudent = async (requestOptions) =>{
    try {
        const response = await fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Students', requestOptions);
        const result = await response.json();
        return result;
    } catch (error) {
        console.log(error);
    }
}

fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Students/id', requestOptions)
                    .then(response => response.json());
