import React from 'react';
class DDClassName extends React.Component {
    state = {
      teams: [],
      selectedTeam: "",
      validationError: ""
    };
  
    componentDidMount() {
      fetch(
        "http://localhost:55217/api/folioclasses"
      )
        .then(response => {
          return response.json();
        })
        .then(data => {
          let teamsFromApi = data.map(team => {
            return { value: id, display: name };
          });
          this.setState({
            teams: [
              {
                value: "",
                display:
                  "(Select Class)"
              }
            ].concat(teamsFromApi)
          });
        })
        .catch(error => {
          console.log(error);
        });
    }
  
    render() {
      return (
        <div>
          <select
            value={this.state.selectedTeam}
            onChange={e =>
              this.setState({
                selectedTeam: e.target.value,
                validationError:
                  e.target.value === ""
                    ? "You must select your favourite team"
                    : ""
              })
            }
          >
            {this.state.teams.map(team => (
              <option
                key={team.value}
                value={team.value}
              >
                {team.display}
              </option>
            ))}
          </select>
          <div
            style={{
              color: "red",
              marginTop: "5px"
            }}
          >
            {this.state.validationError}
          </div>
        </div>
      );
    }
  }

  export default DDClassName;
