import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';


class ClassesList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            clist: []
        };
    }

    async componentDidMount(){
        try{
        const response = await fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Classes');
        const json = await response.json();
        this.setState({ data: json });
        }catch (error) {
            console.log(error);
          }
      
    }

    render() {
        
        if (this.state.clist != "") {
            
            return (
                <div className="table-wrap">
                    <table className="wrapped confluenceTable tablesorter tablesorter-default">
                        <thead>
                            <tr className="tablesorter-headerRow">
                                <th>
                                    Class Name
                        </th>
                                <th>
                                    Location
                        </th>
                                <th>
                                    Teacher Name
                        </th>
                                <th>

                                </th>
                                <th>

                                </th>

                            </tr>
                        </thead>
                        <tbody>
                           
                              
                        


                        </tbody>

                    </table>
                </div>
            )
        }
        else {
            return (
                <div>
                    <p>OOPS!! Something went wrong</p>
                </div>
            )
        }
    }





}

export default ClassesList;
