import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';


class ClassList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            clist: [props]
        };
    }

    render() {
        return (
            <div className="table-wrap">
                <table className="wrapped confluenceTable tablesorter tablesorter-default">
                    <thead>
                        <tr className="tablesorter-headerRow">
                            <th>Class Name</th>
                            <th>Location</th>
                            <th>Teacher Name</th>
                            <th>

                            </th>
                            <th>

                            </th>

                        </tr>
                    </thead>
                    <tbody>
                        {
                            props.clist.map(el => (
                                <tr key={el.clsId}>

                                    <td onClick={() => setRowId(el.clsId)} style={{ cursor: 'pointer' }}>
                                        {el.clsName}
                                    </td>
                                    <td onClick={() => setRowId(el.clsId)} style={{ cursor: 'pointer' }}>
                                        {el.clsLocation}
                                    </td>
                                    <td>
                                        {el.clsSalutations} {el.clsTLName}
                                    </td>
                                    <td>
                                        edit
                                </td>
                                    <td>
                                        <button className="btn btn-light" onClick={deleteClass(el.clsId)}>Delete</button>
                                    </td>
                                </tr>
                            ))
                        }
                        <tr>
                            <button type="button" class="btn btn-light">ADD</button>
                        </tr>
                    </tbody>

                </table>

                {rowId > 0 ? <StudentList rowId={rowId} /> : " "}


            </div>




        );
    }




}

export default ClassesList;
