import React, { useState, useEffect } from 'react';
import { Redirect, Link} from 'react-router-dom';

const StudentAdd = props => {
    const initialFormState = { id: null, firstName: '', lastName: '', age: '', gpa: '', folioClassId: 0 }
    const [user, setUser] = useState(initialFormState);
    const [clst, setClassData] = useState([]);


    React.useEffect(function effectFunction() {
        async function fetchClasses() {
            const response = await fetch('http://localhost:55217/api/folioclasses');
            const json = await response.json();
            setClassData(json.item1);
        }
        fetchClasses();
    }, []);

    const handleInputChange = event => {
        const { name, value } = event.target;

        if (name === "age") {
            if (value < 1 || value > 100) {
                user.age = "";
                alert("Not valid age");
            }
        }

        if (name === "lastName") {

            if (props.stuList.map(el => (el.lastName).toUpperCase()).indexOf(value.toUpperCase()) !== -1) {

                alert("Please change the last name");
                user.lastName = "";

            } else {

                setUser({ ...user, [name]: value });

            }
        }
        else {
            setUser({ ...user, [name]: value });
        }

    }

    return (
        <form
            onSubmit={event => {
                event.preventDefault()
                if (!user.firstName || !user.lastName || !user.age || !user.gpa) {
                    alert("Fill Form");
                    return
                }
                const requestOptions = {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(user)
                };
                fetch('http://localhost:55217/api/Students', requestOptions)
                    .then(response => response.json());
                console.log(JSON.stringify(user));

                setUser(initialFormState)

            }}>
            <table className="table table-bordered table-sm ">
                <tr>
                    <td>
                        <span>First Name</span>
                    </td>
                    <td>
                        <input size="30" type="text" name="firstName" value={user.firstName} onChange={handleInputChange} />

                    </td>
                </tr>
                <tr>
                    <td>
                        <span>Last Name</span>
                    </td>
                    <td>
                        <input size="30" type="text" name="lastName" value={user.lastName} onChange={handleInputChange} />
                    </td>
                </tr>
                <tr>
                    <td> <span>Age</span></td>
                    <td><input type="number" pattern="[0-9]*" inputmode="numeric" name="age" value={user.age} onChange={handleInputChange} /></td>
                </tr>
                <tr>
                    <td><span>GPA</span></td>
                    <td><input type="number" pattern="[0-9]*" inputmode="numeric" name="gpa" value={user.gpa} onChange={handleInputChange} /></td>
                </tr>
                <tr>
                    <td>
                        <span>Class Name</span>
                    </td>
                    <td>
                        <select name="clsClass" value="1" className="form-control" >
                            <option value="1">S</option>
                            <option value="1">React</option>
                            <option value="2">Angular</option>
                            <option value="3">MVC</option>                           
                        </select>
                    </td>
                </tr>
                <tr>
                    <td>
                        <button>Add Student</button>&nbsp;&nbsp; <Link to="/" className="btn btn-primary">Back to Main</Link>
                    </td>
                </tr>
            </table>
        </form >
    );
}

export default StudentAdd;
