import React from 'react';
import StudentList from '../students/StudentList';
import { Redirect } from "react-router-dom";


class ClassesList extends React.Component {
    constructor(prop) {
        super(prop);
        this.state = { error: null, isLoaded: false, items: [], rid: 0, redirectToAddClassPage: false };
    }

    async componentDidMount() {
        try {
            const response = await fetch('http://localhost:55217/api/folioclasses');
            const result = await response.json();
            this.setState({ isLoaded: true, items: result.item1 });
        } catch (error) {
            this.setState({ error: true })
            console.log(error);
        }
    }

    deleteClass(id) {

        let array = [...this.state.items]; // make a separate copy of the array
        let index = array.findIndex(x => x.id === id);
        if (index !== -1) {
            array.splice(index, 1);
            this.setState({ items: array });
        }
    }



    handleClick(id) {

        this.setState({ rid: id });
    }

    handleEdit(id) {
        alert(id);
    }


    render() {

        let { error, isLoaded, items } = this.state;
        if (error) {
            return (
                <div>
                    <p>
                        Error: {error}
                    </p>
                </div>
            )
        }
        else {
            return (
                <div>
                    <p>Class Data</p>
                    <table className="table table-bordered">
                        <thead>
                            <tr>
                                <th> Class Name</th>
                                <th>Location</th>
                                <th>Teacher Name</th>
                                <th></th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                items.map(el => (
                                    <tr key={el.id}>
                                        <td onClick={() => this.handleClick(el.id)} style={{ cursor: 'pointer' }}>
                                            {el.name}
                                        </td>
                                        <td onClick={() => this.handleClick(el.id)} style={{ cursor: 'pointer' }}>
                                            {el.location}
                                        </td>
                                        <td>
                                            {el.teacherName}
                                        </td>
                                        <td>           
                                        <Link
  to={{
    pathname: "/courses",
    search: "?sort=name",
    hash: "#the-hash",
    state: { fromDashboard: true }
  }}
/>                                 
                                            <a Component="/classesForm/{el.id}" onClick={() => this.handleEdit(el.id)} >Edit </a>
                                         </td>
                                        <td>
                                            <button onClick={() => this.deleteClass(el.id)} className="btn btn-light">
                                                Delete
                                            </button>
                                        </td>
                                    </tr>
                                    ))
                            }
                             <tr>
                                <td>
                                    {this.state.redirectToAddClassPage && <Redirect to="/classesform" />}
                                    <button type="button" onClick={() => this.setState({ redirectToAddClassPage: true })} >ADD</button>

                                </td>
                            </tr>                           
                            <tr>
                                <td>{this.state.rid > 0 ? <StudentList rowId={this.state.rid} /> : " "}</td>
                            </tr>
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}
export default ClassesList;
