import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';


class ClassesList extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            clist: props
        };
    }

    render() {
        return (
            <div className="table-wrap">
                <table className="wrapped confluenceTable tablesorter tablesorter-default">
                    <thead>
                        <tr className="tablesorter-headerRow">
                            <th>Class Name</th>
                            <th>Location</th>
                            <th>Teacher Name</th>
                            <th></th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.clist.map(el => (
                                <tr key={el.clsId}>

                                    <td>
                                        {el.clsName}
                                    </td>
                                    <td>
                                        {el.clsLocation}
                                    </td>
                                    <td>
                                        {el.clsSalutations} {el.clsTLName}
                                    </td>
                                    <td>
                                        edit
                                </td>
                                    <td>
                                        <button className="btn btn-light" >Delete</button>
                                    </td>
                                </tr>
                            ))
                        }
                        <tr>
                            <button type="button" class="btn btn-light">ADD</button>
                        </tr>
                    </tbody>

                </table>

                {/*rowId > 0 ? <StudentList rowId={rowId} /> : " "*/}


            </div>




        );
    }




}

export default ClassesList;
