import React from 'react';

class ClassesForm extends React.Component {
    constructor(props) {
        super(props);       
        this.state = {};

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
       
    }

    handleChange(event) {
        this.setState({[event.target.name]: event.target.value});
      }

      handleSubmit(event) {
        event.preventDefault();
        debugger;

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify()
        };
        fetch('http://localhost:55217/api/folioclasses', requestOptions)
            .then(response => response.json());
        console.log(JSON.stringify());

       
      }
   
    render() {
        return (
            <form onSubmit={this.handleSubmit}>

                <tr>
                    <td><span>Class Name:</span></td>
                    <td><input type="text" name="cname" value={this.state.cName} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td><span>Location:</span></td>
                    <td><input type="text" name="location" value={this.state.location} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td><span>First Name:</span></td>
                    <td><input type="text" name="fName" value={this.state.fName} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td><span>Last Name:</span></td>
                    <td><input type="text" name="lName" value={this.state.lName} onChange={this.handleChange}></input>

                    </td>
                </tr>
                <tr>
                    <td>
                        Select
                  </td>

                    <td>
                        <select value={this.state.value} onChange={this.handleChange}>
                            <option value="Mr">Mr</option>
                            <option value="Miss">MIss</option>
                            <option value="Dr">Dr</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td>
                        <input type="submit" value="Submit" />
                    </td>
                </tr>
            </form>
        );
    }
}

export default ClassesForm;
