import React from 'react';

class ClassesForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: [] };

       
    }

    handleFirstNameChange = event => {
        this.setState({
          firstName: event.target.value
        });
      }
    render() {
        return (
            <form onSubmit={this.handleSubmit}>

                <tr>
                    <td><span>Class Name:</span></td>
                    <td><input type="text" name="cname" value={this.state.firstName} onChange=""></input>

                    </td>
                </tr>
                <tr>
                    <td><span>Location:</span></td>
                    <td><input type="text" name="location" value="" onChange=""></input>

                    </td>
                </tr>
                <tr>
                    <td><span>First Name:</span></td>
                    <td><input type="text" name="fName" value="" onChange=""></input>

                    </td>
                </tr>
                <tr>
                    <td><span>Last Name:</span></td>
                    <td><input type="text" name="lName" value="" onChange=""></input>

                    </td>
                </tr>
                <tr>
                    <td>
                        Select
                  </td>

                    <td>
                        <select value={this.state.value} onChange={this.handleChange}>
                            <option value="Mr">Mr</option>
                            <option value="Miss">MIss</option>
                            <option value="Dr">Dr</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <td>
                        <input type="submit" value="Submit" />
                    </td>
                </tr>
            </form>
        );
    }
}

export default ClassesForm;
