import React from "react";
import PropTypes from "prop-types";


const ClassesForm = ({
  folioClasses,
  onSave,
  onChange,
  saving = false,
  errors = {}
}) => {
  return (
    <form onSubmit={onSave}>
      <h2>{folioClasses.id ? "Edit" : "Add"} Class</h2>
      {errors.onSave && (
        <div className="alert alert-danger" role="alert">
          {errors.onSave}
        </div>
      )}

      <input type="text" name="Name" label="Name" value={folioClasses.name} onChange={onChange} error={errors.title}></input>

   

      <TextInput
        name="Name"
        label="Name"
        value={folioClasses.name}
        onChange={onChange}
        error={errors.title}
      />      

      <TextInput
        name="Location"
        label="Location"
        value={folioClasses.location}
        onChange={onChange}
        error={errors.category}
      />

      <button type="submit" disabled={saving} className="btn btn-primary">
        {saving ? "Saving..." : "Save"}
      </button>
    </form>
  );
};

ClassesForm.propTypes = {
  folioClasses: PropTypes.object.isRequired,
  errors: PropTypes.object,
  onSave: PropTypes.func.isRequired,
  onChange: PropTypes.func.isRequired,
  saving: PropTypes.bool
};

export default ClassesForm;
