import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import ClassesForm from "./ClassesForm";

function ManageClassesPage({
    folioClasses,
    loadClasses,
    saveCourse,
    history,
    ...props
}) {
    const [folioClass, setClasses] = useState({ ...props.folioClass });
    const [errors, setErrors] = useState({});

    useEffect(() => {
        if (courses.length === 0) {
            loadCourses().catch(error => {
                alert("Loading courses failed" + error);
            });
        } else {
            setClasses({ ...props.folioClass });
        }


    }, [props.folioClass]);

    function handleChange(event) {
        const { name, value } = event.target;
        setCourse(prevCourse => ({
            ...prevCourse,
            [name]: name === "authorId" ? parseInt(value, 10) : value
        }));
    }

    function handleSave(event) {
        event.preventDefault();
        saveClass(course).then(() => {
            history.push("/classes");
        });
    }

    return (
        <ClassesForm
            folioClass={folioClass}
            errors={errors}
            onChange={handleChange}
            onSave={handleSave}
        />
    );
}

ManageClassesPage.propTypes = {
    folioClass: PropTypes.object.isRequired,
    
    folioClasses: PropTypes.array.isRequired,
    loadClasses: PropTypes.func.isRequired,    
    saveCourse: PropTypes.func.isRequired,
    history: PropTypes.object.isRequired
};

export function getCourseBySlug(courses, slug) {
    return courses.find(course => course.slug === slug) || null;
}

function mapStateToProps(state, ownProps) {
    const slug = ownProps.match.params.slug;
    const course =
        slug && state.courses.length > 0
            ? getCourseBySlug(state.courses, slug)
            : newCourse;
    return {
        course,
        courses: state.courses,
        authors: state.authors
    };
}

const mapDispatchToProps = {
    loadCourses,
    loadAuthors,
    saveCourse
};

export default ManageCoursePage;
