import React, { useState } from 'react'

const StudentAdd = props => {
    const initialFormState = { id: null, firstName: '', lastName: '', age: '', gpa: '', folioClassId: '' }
    const [user, setUser] = useState(initialFormState)

    const handleInputChange = event => {
        const { name, value } = event.target;
    

        if(props.name.map(el =>(el.TeacherLName).toUpperCase()).indexOf(user.SLName.toUpperCase())!== -1){
                 
            alert("Please change the last name");
        }else{
            
            setUser({ ...user, [name]: value });

        }


        
    }

    return (
        <form
            onSubmit={event => {
                event.preventDefault()
                if (!user.firstName || !user.SLName || !user.Sage || !user.Sgpa) return

                const requestOptions = {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(user)
                };
                fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Student', requestOptions)
                    .then(response => response.json());
                    console.log(JSON.stringify(user));
                    
                setUser(initialFormState)
            }}
        >
            <tr>
                <td>
                    <label>First Name</label>

                </td>
                <td>
                    <input type="text" name="SFName" value={user.SFName} onChange={handleInputChange} />

                </td>
            </tr>

            <tr>
                <td>
                    <label>Last Name</label>
                </td>
                <td>
                    <input type="text" name="SLName" value={user.SLName} onChange={handleInputChange} />
                </td>
            </tr>
            <tr>
                <td> <label>Age</label></td>
                <td><input type="text" name="Sage" value={user.Sage} onChange={handleInputChange} /></td>
            </tr>
            <tr>
                <td><label>GPA</label></td>
                <td><input type="text" name="Sgpa" value={user.Sgpa} onChange={handleInputChange} /></td>
            </tr>
            <tr>
                <td>
                    <button>Add new user</button>
                </td>
            </tr>






        </form>
    )
}

export default StudentAdd;
