import React, { useState } from 'react'

const StudentAdd = props => {
    const initialFormState = { id: null, firstName: '', lastName: '', age: '', gpa: '', folioClassId: '' }
    const [user, setUser] = useState(initialFormState)

    const handleInputChange = event => {
        const { name, value } = event.target;

        if(name === "age"){
            if(value < 1 || value > 100){
                user.age = "";
                alert
            }
        }

        if (name === "lastName") {

            if (props.stuList.map(el => (el.lastName).toUpperCase()).indexOf(value.toUpperCase()) !== -1) {

                alert("Please change the last name");
                user.lastName = "";
                
            } else {

                setUser({ ...user, [name]: value });

            }
        }
        else{
            setUser({ ...user, [name]: value });
        }



    }

    return (
        <form
            onSubmit={event => {
                event.preventDefault()
                if (!user.firstName || !user.lastName || !user.age || !user.gpa || user.folioClassId) return
                const requestOptions = {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(user)
                };
                fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Student', requestOptions)
                    .then(response => response.json());
                console.log(JSON.stringify(user));

                setUser(initialFormState)


            }}
        >
            <tr>
                <td>
                    <label>First Name</label>

                </td>
                <td>
                    <input type="text" name="firstName" value={user.firstName} onChange={handleInputChange} />

                </td>
            </tr>

            <tr>
                <td>
                    <label>Last Name</label>
                </td>
                <td>
                    <input type="text" name="lastName" value={user.lastName} onChange={handleInputChange} />
                </td>
            </tr>
            <tr>
                <td> <label>Age</label></td>
                <td><input type="number" pattern="[0-9]*" inputmode="numeric" name="age" value={user.age} onChange={handleInputChange} /></td>
            </tr>
            <tr>
                <td><label>GPA</label></td>
                <td><input type="text" name="gpa" value={user.gpa} onChange={handleInputChange} /></td>
            </tr>
            <tr>
                <td>
                    <button>Add Student</button>
                </td>
            </tr>






        </form>
    )
}

export default StudentAdd;
