import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import ClassesForm from "./ClassesForm";

function ManageClassesPage({
    folioClasses,
    loadClasses,
    saveCourse,
    history,
    ...props
}) {
    const [folioClass, setClasses] = useState({ ...props.folioClass });
    const [errors, setErrors] = useState({});

    useEffect(() => {
        if (folioClasses.length === 0) {
           //check from main page
        } 
        else {
            setClasses({ ...props.folioClass });
        }


    }, [props.folioClass]);

    function handleChange(event) {
        const { name, value } = event.target;
        setClasses(prevClass => ({
            ...prevClass,
            [name]: name === "authorId" ? parseInt(value, 10) : value
        }));
    }

    function handleSave(event) {
        event.preventDefault();
        saveClass(course).then(() => {
            history.push("/classes");
        });
    }

    return (
        <ClassesForm
            folioClass={folioClass}
            errors={errors}
            onChange={handleChange}
            onSave={handleSave}
        />
    );
}

ManageClassesPage.propTypes = {
    folioClass: PropTypes.object.isRequired,
    
    folioClasses: PropTypes.array.isRequired,
    loadClasses: PropTypes.func.isRequired,    
    saveCourse: PropTypes.func.isRequired,
    history: PropTypes.object.isRequired
};

export function getCourseBySlug(courses, slug) {
    return courses.find(course => course.slug === slug) || null;
}





export default ManageClassesPage;
