import React,  { useState } from 'react';
import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';

const ClassesList = (props) => {
    const [rowId, setRowId] = useState(0);
    
    return (
        <div className="table-wrap">
            <table className="wrapped confluenceTable tablesorter tablesorter-default">
                <thead>
                    <tr className="tablesorter-headerRow">
                        <th>
                            Class Name
                        </th>
                        <th>
                            Location
                        </th>
                        <th>
                            Teacher Name
                        </th>
                        <th>

                        </th>
                        <th>

                        </th>

                    </tr>
                </thead>
                <tbody>
                    {
                        props.clist.map(el => (
                            <tr key={el.clsId}>
                              
                              <td onClick={() => setRowId(el.clsId)}>
                                    {el.clsName}
                                </td>
                                <td>
                                    {el.clsLocation}
                                </td>
                                <td>
                                    {el.clsSalutations} {el.clsTLName}
                                </td>
                                <td>
                                    edit
                                </td>
                                <td>
                                    delete
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>     
      
            {rowId > 0 ? <StudentList  /> : " " }
          
        
        </div>

        

      
    );

    


}

export default ClassesList;
