import React from 'react';

class StudentList extends React.Component {
    constructor(props) {
        super(props);
        this.state = { error: null, isLoaded: false, items: [], gpaArray:[], highestGPA:0.0 };
    }

    async componentDidMount() {
        try {
            const response = await fetch('http://localhost:55217/api/Students');
            const result = await response.json();
            this.setState({ isLoaded: true, items: result.item1 });
                  } catch (error) {
            this.setState({ error: true })
            console.log(error);
        }
    }

    deleteStudent(id) {
        let array = [...this.state.items]; // make a separate copy of the array
        let index = array.findIndex(x => x.id === id);
        if (index !== -1) {
            array.splice(index, 1);
            this.setState({ items: array });
        }
    }

    render() {
        const { error, isLoaded, items } = this.state;
        if (error) {
            return (
                <div>
                    <p>
                        Error: {error}
                    </p>
                </div>
            )
        }
        else {
            return (

                <div>
                    <p>Student Data </p>
                    <table className="table table-bordered table-sm">
                        <thead className="thead-light">

                            <tr>
                                <th> Student Name</th>
                                <th>Age</th>
                                <th>GPA</th>
                                <th></th>
                                <th></th>
                            </tr>
                        </thead>
                        <tbody>
                            
                            {/*this.setState({gpaArray: (items.filter((student) => { return student.folioClassId === parseInt(this.props.rowId) }).map(el => el.gpa))})*/}
                            
                            {                               
                                items.filter((student) => { return student.folioClassId === parseInt(this.props.rowId) }).map(el => (
                                    

                                    <tr key={el.id}>
                                       
                                        <td>{el.firstName} {el.lastName}</td>                                   
                                       
                                        <td>
                                            {el.age}
                                        </td>
                                        <td>
                                            {el.gpa}
                                        </td>
                                        <td>
                                            edit
                                        </td>
                                        <td>
                                            <button onClick={() => this.deleteStudent(el.id)}  className="btn btn-light">
                                                     Delete
                                            </button>
                                        </td>
                                    </tr>
                                ))
                            }
                            <tr>
                                <button type="button" className="btn btn-light">ADD</button>
                            </tr>
                        </tbody>
                    </table>
                </div>
            );
        }
    }
}

export default StudentList;
