import React from 'react';
import ReactDOM from 'react-dom';
import PropTypes from "prop-types";
import { toast } from "react-toastify";


class StudentList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            slist: []

        };

    }

    componentDidMount() {
        fetch('https://6079395e460a6600174fb472.mockapi.io/api/v1/Students/')
            .then(res => res.json())
            .then((data) => {
                this.setState({ slist: data })
            })
            .catch(console.log)

    }

    deleteStudent(id){
        alert(id);
    }



    render() {
        return (

            <div>
                <p>Student Data </p>
                <table className="table table-bordered">
                    <thead>

                        <tr>
                            <th> Student Name</th>
                            <th>Age</th>
                            <th>GPA</th>
                            <th></th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.slist.filter((student) => { return student.clsId === parseInt(this.props.rowId) }).map(el => (

                                <tr key={el.stuId}>
                                    <td>
                                        {el.stuFName} {el.stuLName}
                                    </td>
                                    <td>
                                        {el.stuAge}
                                    </td>
                                    <td>
                                        {el.stuGpa}
                                    </td>
                                    <td>
                                        edit
                                </td>
                                    <td>
                                        <button
                                            onClick={() => this.deleteStudent(el.stuId)}
                                            className="button muted-button"
                                        >
                                            Delete
                                        </button>
                                    </td>
                                </tr>
                            ))
                        }
                        <tr>
                            <button type="button" class="btn btn-light">ADD</button>
                        </tr>
                    </tbody>
                </table>
            </div>
        );
    }
}

export default StudentList;
