import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import StudentList from '../students/StudentList';


class ClassesList extends React.Component {

    constructor(props) {
        super(props);     
        this.state = {
            data: []
        };   
    }

    async componentDidMount(){
        const url = 'http://localhost:55217/api/folioclasses';
        try{
        const response = await fetch(url);
        const json = await response.json();
        this.setState({ data: json });
        }catch (error) {
            console.log(error);
          }
          console.log('component mount');      
    }

    render() {
            return (
                <div className="table-wrap">
                    <table className="wrapped confluenceTable tablesorter tablesorter-default">
                        <thead>
                            <tr className="tablesorter-headerRow">
                                <th>
                                    Class Name
                        </th>
                                <th>
                                    Location
                        </th>
                                <th>
                                    Teacher Name
                        </th>
                                <th>

                                </th>
                                <th>

                                </th>

                            </tr>
                        </thead>
                        <tbody>
                    {
                        this.state.data.splitmap(el => (
                            <tr key={el.clsId}>

                                <td style={{ cursor: 'pointer' }}>
                                    {el.clsName}
                                </td>
                                <td  style={{ cursor: 'pointer' }}>
                                    {el.clsLocation}
                                </td>
                                <td>
                                    {el.clsSalutations} {el.clsTLName}
                                </td>
                                <td>
                                    edit
                                </td>
                                <td>
                                    <button className="btn btn-light" >Delete</button>
                                </td>
                            </tr>
                        ))
                    }
                    <tr>
                        <td>
                        <button type="button" className="btn btn-light">ADD</button>
                        </td>
                    </tr>
                </tbody>

                    </table>
                </div>
            )
        }    

}

export default ClassesList;
